parameters.text:
=Analyze if this conversation contains information worth remembering long-term.

Current context:
={{ $json.context }}

Latest exchange:
User: {{ $json.userMessage }}
Assistant: {{ $json.assistantResponse }}

Respond with ONLY valid JSON:
{"should_update": true, "new_memories": [{"type": "preference", "content": "User likes pizza"}], "remove_indices": []}

Only create memories for important lasting information (preferences, facts, goals). Not for casual greetings.

parameters.jsCode:
// Run Once for All Items

// Inputs
const trigger = $('Start').first().json || {};
const transcriptRows = $('Fetch Transcript').all().map(i => i.json || {});
const summaryRows = $('Fetch Topic Summaries').all().map(i => i.json || {});

// Filter and sort transcript messages
const rows = transcriptRows
  .filter(r => !r.context_id || String(r.context_id) === String(trigger.context_id))
  .sort((a, b) => {
    const ts = v => Date.parse(v || '') || 0;
    return ts(a.created_at || a.createdAt) - ts(b.created_at || b.createdAt);
  });

// Convert rows to context.recent_messages
const recent_messages = rows.map(r => {
  const message = String(r.message ?? r.message_original ?? '').trim();
  const original = String(r.message_original ?? '').trim();

  return {
    id: r.id,
    role: r.role === 'assistant' ? 'assistant' : 'user',
    content: message,
    was_truncated: original.length > 0 && message !== original
  };
});

// Convert summaries (reverse for chronological order)
const topic_summaries = summaryRows
  .reverse()
  .map(s => ({
    topic_title: s.topic_title || '',
    summary: s.topic_summary || '',
    message_range: [s.first_message_id, s.last_message_id],
    message_count: s.message_count || 0
  }));

// Keep any existing long-term memories
let long_term_memories = [];
try {
  if (trigger.context && typeof trigger.context === 'string') {
    const parsed = JSON.parse(trigger.context);
    if (Array.isArray(parsed?.long_term_memories)) {
      long_term_memories = parsed.long_term_memories;
    }
  } else if (trigger.context && typeof trigger.context === 'object' &&
             Array.isArray(trigger.context.long_term_memories)) {
    long_term_memories = trigger.context.long_term_memories;
  }
} catch { /* ignore */ }

const context = { 
  topic_summaries,      // NEW: Historical topic summaries
  recent_messages, 
  long_term_memories 
};

return [
  {
    json: {
      context_id: String(trigger.context_id || ''),
      context: JSON.stringify(context),
      userMessage: String(trigger.userMessage ?? ''),
      assistantResponse: String(trigger.assistantResponse ?? '')
    }
  }
];

// Configuration
const MESSAGE_LENGTH_THRESHOLD = 5000;
const TRUNCATE_LENGTH = 1000;
const TRUNCATION_SUFFIX = '... [truncated] Message Summary: ';

// Get input data from trigger
const triggerData = $('Start').first().json;
const contextId = triggerData.context_id;
const userMessage = triggerData.userMessage;
const assistantResponse = triggerData.assistantResponse;
const tablePrefix = triggerData.table_prefix;
const contextStr = triggerData.context;

// Process user message
let userItem = null;
if (typeof userMessage === 'string' && userMessage.trim()) {
  const originalMessage = userMessage.trim();
  const messageLength = originalMessage.length;

  userItem = {
    context_id: contextId,
    table_prefix: tablePrefix,
    context: contextStr,
    role: 'user',
    message_original: originalMessage,
    needs_summary: messageLength > MESSAGE_LENGTH_THRESHOLD,
    truncated_message: messageLength > MESSAGE_LENGTH_THRESHOLD 
      ? originalMessage.substring(0, TRUNCATE_LENGTH) + TRUNCATION_SUFFIX
      : null,
    message: messageLength <= MESSAGE_LENGTH_THRESHOLD ? originalMessage : null
  };
}

// Process assistant message
let assistantItem = null;
if (typeof assistantResponse === 'string' && assistantResponse.trim()) {
  const originalMessage = assistantResponse.trim();
  const messageLength = originalMessage.length;

  assistantItem = {
    context_id: contextId,
    table_prefix: tablePrefix,
    context: contextStr,
    role: 'assistant',
    message_original: originalMessage,
    needs_summary: messageLength > MESSAGE_LENGTH_THRESHOLD,
    truncated_message: messageLength > MESSAGE_LENGTH_THRESHOLD 
      ? originalMessage.substring(0, TRUNCATE_LENGTH) + TRUNCATION_SUFFIX
      : null,
    message: messageLength <= MESSAGE_LENGTH_THRESHOLD ? originalMessage : null
  };
}

// Return items for processing
const items = [];
if (userItem) items.push({ json: userItem });
if (assistantItem) items.push({ json: assistantItem });

return items;

// === Combine Message with Summary (per item) ===

// Direct agent summary from previous node
const summary = String($input.item?.json?.output ?? '').trim();

// Nearby items on the same branch (if present)
const paired = (name) => { try { return ($item(name, 0)?.json ?? {}); } catch { return {}; } };
const cur    = $json ?? {};
const merged = paired('Merge Messages');            // rename if needed
const prep   = paired('Prepare Transcript Rows');   // rename if needed

// Role: from the current item first
const role = String(cur.role ?? merged.role ?? prep.role ?? 'user').trim();

// Trigger payload (never used for role, only as last-resort text)
const trigger = $('Start').first().json || {};
const triggerMessage =
  role === 'assistant'
    ? String(trigger.assistantResponse ?? '')
    : String(trigger.userMessage ?? '');

// Original message: current item -> nearby items -> trigger (role-aware)
const originalRaw =
  cur.message_original ?? cur.message ??
  merged.message_original ?? merged.message ??
  prep.message_original ?? prep.message ??
  triggerMessage;

const original = String(originalRaw ?? '').trim();

// Minimal required meta
const context_id   = String(cur.context_id   ?? merged.context_id   ?? prep.context_id   ?? trigger.context_id ?? '');
const table_prefix = String(cur.table_prefix ?? merged.table_prefix ?? prep.table_prefix ?? trigger.table_prefix ?? '');
const context      = (cur.context ?? merged.context ?? prep.context ?? trigger.context ?? null);

// Build combined message
const MAX = 1000;
const SUFFIX = ' ... [truncated] Message Summary: ';
const finalMessage = original.slice(0, MAX) + SUFFIX + summary;

// Output with message_original populated
return {
  json: {
    context_id,
    table_prefix,
    context,
    role,
    message_original: original,
    message: finalMessage,
  }
};

// Message doesn't need summary, use original as-is
// Pass through all context data
return {
  json: {
    context_id: $json.context_id,
    table_prefix: $json.table_prefix,
    context: $json.context,
    role: $json.role,
    message_original: $json.message_original,
    message: $json.message
  }
};

// Process topic detection result
const trigger = $('Start').first().json || {};
const aiOutput = $json.output || '{}';

// Parse AI response
let result = { is_topic_shift: false, confidence: 0, new_topic_name: null, reasoning: '' };
try {
  const cleaned = aiOutput
    .replace(/```json\n?/g, '')
    .replace(/```\n?/g, '')
    .trim();
  result = JSON.parse(cleaned);
} catch (e) {
  console.log('Failed to parse AI response:', aiOutput);
}

const CONFIDENCE_THRESHOLD = 0.7;
const isShift = result.is_topic_shift && result.confidence >= CONFIDENCE_THRESHOLD;

// Log the result
console.log('=== TOPIC DETECTION ===' );
console.log('Context ID:', trigger.context_id);
console.log('Topic Shift:', isShift);
console.log('Confidence:', result.confidence);
console.log('New Topic:', result.new_topic_name);
console.log('Reasoning:', result.reasoning);
if (isShift) {
  console.log('⚠️  TOPIC CHANGED - State will be reset');
} else {
  console.log('✓ Continuing current topic');
}
console.log('=======================');

// Pass through for state update
return [{
  json: {
    context_id: trigger.context_id,
    topic_shift_detected: isShift,
    shift_confidence: result.confidence,
    suggested_topic_name: result.new_topic_name || 'Untitled Topic',
    shift_reasoning: result.reasoning
  }
}];

// Combine all data for a SINGLE detection call
const trigger = $('Start').first().json || {};
const recentMessages = $input.all().map(i => i.json || {});

// Format recent messages (excluding the new ones we just added)
const contextMessages = recentMessages
  .filter(m => m.id) // has ID = already in DB
  .sort((a, b) => a.id - b.id) // oldest to newest
  .slice(0, -2) // exclude last 2 (the ones we just inserted)
  .map(m => `${m.role}: ${m.message}`)
  .join('\n');

// The NEW exchange we're analyzing
const newUserMsg = trigger.userMessage || '';
const newAssistantMsg = trigger.assistantResponse || '';

// Build prompt
const prompt = `Analyze if this new exchange is a topic shift from previous conversation.

Previous context:
${contextMessages || 'No previous messages'}

New exchange to analyze:
user: ${newUserMsg}
assistant: ${newAssistantMsg}

Did the topic change? Respond ONLY with JSON:
{
  "is_topic_shift": true or false,
  "confidence": 0.0 to 1.0,
  "new_topic_name": "3-5 words" or null,
  "reasoning": "one sentence"
}

Rules: shift = NEW subject. NOT shift = continuation of same subject.`;

return [{
  json: {
    context_id: trigger.context_id,
    detection_prompt: prompt
  }
}];

// Prepare messages for summarization
const stateData = $('Update Topic State').first().json;
const messages = $input.all().map(i => i.json);

// Format conversation
const conversation = messages.map(m => {
  const content = m.message_original || m.message;
  return `${m.role}: ${content}`;
}).join('\n\n');

// Build summarization prompt
const prompt = `Create a concise summary of this conversation topic.

Conversation (${messages.length} messages):
${conversation}

Provide a JSON response (no markdown, no code blocks):
{
  "topic_title": "Brief 5-8 word description",
  "main_summary": "2-3 sentence overview",
  "key_points": ["point 1", "point 2", "point 3"],
  "decisions_made": ["decision 1"] or [],
  "action_items": ["action 1"] or []
}

Guidelines:
- Keep concise and focused
- Capture main ideas and important details
- Omit greetings and casual chat`;

return [{
  json: {
    context_id: stateData.context_id,
    first_message_id: messages[0].id,
    last_message_id: messages[messages.length - 1].id,
    message_count: messages.length,
    previous_topic_name: stateData.previous_topic_name,
    summarization_prompt: prompt
  }
}];

// Format the AI summary for storage
const summaryInput = $('Prepare Summarization').first().json;
const aiOutput = $json.output || '{}';

// Parse AI response
let parsed;
try {
  const cleaned = aiOutput
    .replace(/```json\n?/g, '')
    .replace(/```\n?/g, '')
    .trim();
  parsed = JSON.parse(cleaned);
} catch (e) {
  console.log('Failed to parse summary:', aiOutput);
  parsed = {
    topic_title: summaryInput.previous_topic_name || 'Topic',
    main_summary: aiOutput.substring(0, 200),
    key_points: [],
    decisions_made: [],
    action_items: []
  };
}

// Format the full summary
let fullSummary = `${parsed.main_summary}\n\n`;

if (parsed.key_points?.length > 0) {
  fullSummary += `Key Points: ${parsed.key_points.join('; ')}\n`;
}

if (parsed.decisions_made?.length > 0) {
  fullSummary += `Decisions: ${parsed.decisions_made.join('; ')}\n`;
}

if (parsed.action_items?.length > 0) {
  fullSummary += `Actions: ${parsed.action_items.join('; ')}`;
}

console.log('=== TOPIC SUMMARY CREATED ===');
console.log('Title:', parsed.topic_title);
console.log('Messages:', summaryInput.first_message_id, 'to', summaryInput.last_message_id);
console.log('Count:', summaryInput.message_count);
console.log('============================');

return [{
  json: {
    context_id: summaryInput.context_id,
    topic_title: parsed.topic_title,
    topic_summary: fullSummary.trim(),
    first_message_id: summaryInput.first_message_id,
    last_message_id: summaryInput.last_message_id,
    message_count: summaryInput.message_count
  }
}];

parameters.query:
INSERT INTO {{ $('Start').first().json.table_prefix }}chat_topic_summaries
(context_id, topic_title, topic_summary, first_message_id, last_message_id, message_count)
VALUES (
  '{{ $json.context_id }}',
  '{{ String($json.topic_title).replace(/'/g, "''") }}',
  '{{ String($json.topic_summary).replace(/'/g, "''") }}',
  {{ $json.first_message_id }},
  {{ $json.last_message_id }},
  {{ $json.message_count }}
);
